    private void btnCadastrarMouseClicked(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_btnCadastrarMouseClicked
        // copia dados dos campos para o objeto carro
        carro.setId(Integer.valueOf(txtId.getText()));
        carro.setNome(txtNome.getText());
        carro.setMarca(txtMarca.getText());
        carro.setCor(txtCor.getText());
        carro.setPreco(Float.valueOf(txtPreco.getText()));
        // conecta com o BD
        conexao.conectaBanco();
        // cria a query para insert
        String query = 
                "INSERT INTO carro (" + 
                " id, nome, marca, cor, preco) " + 
                "VALUES (" + 
                carro.getId() + "," +
                "'" + carro.getNome() + "',"+
                "'" + carro.getMarca() + "',"+
                "'" + carro.getCor() + "',"+
                carro.getPreco() +
                ");";
        // faz o insert no BD
        if (conexao.insertSQL(query) == 1) {
            // sucesso
            JOptionPane.showMessageDialog(this, "Carro cadastrado com sucesso");
        } else {
            // erro
            JOptionPane.showMessageDialog(this, "Erro ao inserir o carro");
        }
        // fecha o banco
        conexao.fechaBanco();
        // limpa a tela
        txtId.setText("");
        txtNome.setText("");
        txtMarca.setText("");
        txtCor.setText("");
        txtPreco.setText("");
    }//GEN-LAST:event_btnCadastrarMouseClicked

